  `id` int(11) unsigned NOT NULL COMMENT 'Primary key',
  `tahapan_id` int(11) unsigned NOT NULL COMMENT 'Id Tahapan Proses: merujuk ke tabel tahapan_proses kolom id',
  `kode` varchar(10) DEFAULT NULL COMMENT 'Kode Proses: isian bebas',
  `nama` varchar(200) DEFAULT NULL COMMENT 'Nama Proses: isian bebas',
  `keterangan` varchar(255) DEFAULT NULL COMMENT 'Keterangan: isian bebas',
  `nama_tabel` varchar(30) DEFAULT NULL COMMENT 'Nama Tabel Tempat Menyimpan Data: (by system)',
  `nama_kolom` varchar(64) DEFAULT NULL COMMENT 'Nama Kolom Tempat Menyimpan Data: (by system)',
  `status` varchar(100) DEFAULT NULL,
  `aktif` char(1) NOT NULL DEFAULT 'Y' COMMENT 'Status Aktif: pilihan Y=Ya; T=Tidak',
  `diedit_oleh` varchar(30) DEFAULT NULL COMMENT 'Diedit Oleh: (by system)',
  `diedit_tanggal` datetime DEFAULT NULL COMMENT 'Diedit Tanggal: (by system)',
  `diinput_oleh` varchar(30) DEFAULT NULL COMMENT 'Diinput Oleh: (by system)',
  `diinput_tanggal` datetime DEFAULT NULL COMMENT 'Diinput Tanggal: (by system)',
  `diperbaharui_oleh` varchar(30) DEFAULT NULL COMMENT 'Diperbaharui Oleh: (by system)',
  `diperbaharui_tanggal` datetime DEFAULT NULL COMMENT 'Diperbaharui Tanggal: (by system)',
  PRIMARY KEY (`id`),
  KEY `kode` (`kode`),
  KEY `nama` (`nama`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Referensi Proses Perkara';

/*Table structure for table `proses_alur_perkara` */

