/* Function  structure for function  `alphanum` */

/*!50003 DROP FUNCTION IF EXISTS `alphanum` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `alphanum`(str text ) RETURNS text CHARSET latin1
    DETERMINISTIC
BEGIN 
								DECLARE i, len SMALLINT DEFAULT 1; 
								DECLARE ret text DEFAULT ''; 
								DECLARE c CHAR(1); 
								SET len = CHAR_LENGTH( str ); 
								REPEAT 
								BEGIN 
									SET c = MID( str, i, 1 ); 
									IF c REGEXP '^[A-Za-z0-9]+$' THEN 
									SET ret=CONCAT(ret,c); 
									END IF; 
									SET i = i + 1; 
								END; 
								UNTIL i > len END REPEAT; 
								RETURN ret; 
							END */$$
DELIMITER ;

/* Function  structure for function  `bulan_romawi` */

/*!50003 DROP FUNCTION IF EXISTS `bulan_romawi` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `bulan_romawi`(bln INT(2)) RETURNS varchar(4) CHARSET latin1
RETURN
							  CASE
							    WHEN bln = 1 THEN 'I'
							    WHEN bln = 2 THEN 'II'
							    WHEN bln = 3 THEN 'III'
