/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `GetHariKerja`(
        param1 DATE,
        param2 DATE
    ) RETURNS int(11)
BEGIN
  DECLARE jml INT;
  DECLARE jmlmod INT;
  DECLARE jmlfloor INT;
  DECLARE harike1 INT;
  DECLARE harike2 INT;
  DECLARE jmllibur INT;
  DECLARE tgl DATE;
  
  SET harike1 = DAYOFWEEK(param1);
  SET harike2 = DAYOFWEEK(param2);
  
  SET jml= DATEDIFF(param2,param1);
  SET jmlfloor = FLOOR(jml/7);
  SET jmlmod = MOD(jml,7);
  SET jml = (jmlfloor * 7) - (2 * jmlfloor) ;
  SET tgl = DATE_ADD(param1, INTERVAL  (jmlfloor * 7) day);
  
  WHILE jmlmod > 0 DO
    SET tgl = DATE_ADD(tgl, INTERVAL  1 day);
    IF (DAYOFWEEK(tgl)>1 and  DAYOFWEEK(tgl)<7 ) THEN 
    	SET jml = jml + 1;
    END IF;
    SET jmlmod = jmlmod - 1;
  END WHILE;
  
  SELECT COUNT(tanggal) into jmllibur from hari_libur where tanggal >param1 and tanggal<=param2
