/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `perkara_jadwal_sidang_reorder`(
        IN var_id BIGINT(20)
    )
BEGIN
    DECLARE l_id          BIGINT;
    DECLARE l_data_count INT;
    DECLARE data_csr INT;
    DECLARE no_more_data INT;
    DECLARE data_csr CURSOR FOR SELECT id FROM perkara_jadwal_sidang where perkara_id=var_id order by tanggal_sidang;
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET no_more_data=1;
    SET l_data_count=0;
    SET no_more_data=0;
    OPEN data_csr;
       data_loop:WHILE(no_more_data=0) DO
         FETCH data_csr INTO l_id;
         IF no_more_data=1 THEN
              LEAVE data_loop;
         END IF;
         SET l_data_count=l_data_count+1;
         update perkara_jadwal_sidang set urutan=l_data_count where id=l_id;
    END WHILE data_loop;
    CLOSE data_csr;
    SET no_more_data=0;
END */$$
